class Cause{constructor(chess){this.chess=chess;this.container=document.createElement("div");this.setupCauseElements()}setupCauseElements(){this.container.classList.add("cause");this.container.innerHTML="&nbsp;"}showCause(cause){this.container.classList.remove("show");this.container.innerHTML=cause;this.container.offsetWidth;this.container.classList.add("show")}hideCause(){this.container.classList.remove("show")}}