class Game{constructor(chess){this.chess=chess;this.teamName="";this.enemyName="";this.play=false;this.playCause="";this.over=false;this.overCause="";this.turn="";this.time={left:0,teamName:""}}setTime(left,teamName){this.time.left=left;this.time.teamName=teamName}setTurn(turn){if(turn.white){this.turn="white"}else if(turn.black){this.turn="black"}}setOver(data){this.over=data.over;if(typeof data.cause){this.overCause=data.cause}}setPlay(data){this.play=data.play;if(typeof data.cause!=="undefined"){this.playCause=data.cause}else{this.playCause=""}}setTeamName(name){this.teamName=name;if(this.teamName==="spectators"){this.chess.actions.hideActions()}}setEnemyName(){switch(this.teamName){case"white":this.enemyName="black";break;case"black":this.enemyName="white";break}}get youArePlayer(){return this.teamName==="white"||this.teamName==="black"}setStart(){this.chess.board.lastMove={}}execOver(){this.chess.actions.offerDrawDecisions.classList.add("hide");this.chess.board.container.removeEventListener("click",this.chess.board.onClickEvent)}}